import React, {Component} from 'react'
import "./MainPage.css";
import Post from '../Post/Post';
import upload from '../../images/images/upload.png';
import {storage,auth} from '../firebase'
import {ref, uploadBytesResumable, getDownloadURL } from "firebase/storage"

class MainPage extends Component{
    constructor(props){
        super(props);
        this.state={
            postArray:[]
        }
    }
    componentDidMount(){
        this.getPost();
    }

    getPost=()=>{
        let data =[
            {
                "postId":"123456",
                "userName":"Khush",
                "postImageURL":"https://images.unsplash.com/photo-1575936123452-b67c3203c357?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8aW1hZ2V8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60",
                "timeStamp":"12345",
                "likes":"1234"

                
            },
            {
                "postId":"123456",
                "userName":"Khush",
                "postImageURL":"https://images.unsplash.com/photo-1575936123452-b67c3203c357?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8aW1hZ2V8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60",
                "timeStamp":"12345",
                "likes":"1234"

                
            },
            {
                "postId":"123456",
                "userName":"Khush",
                "postImageURL":"https://images.unsplash.com/photo-1575936123452-b67c3203c357?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8aW1hZ2V8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60",
                "timeStamp":"12345",
                "likes":"1234"

                
            },
            {
                "postId":"123456",
                "userName":"Khush",
                "postImageURL":"https://images.unsplash.com/photo-1575936123452-b67c3203c357?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8aW1hZ2V8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60",
                "timeStamp":"12345",
                "likes":"1234"

                
            }
        ];
        this.setState({postArray: data});
    }
    upload=(event)=>{

        const storageRef = ref(storage, 'images/images');

        const uploadTask = uploadBytesResumable(storageRef, images);

        uploadTask.on('state_changed', 
  (snapshot) => {
  
    const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
    console.log('Upload is ' + progress + '% done');
    switch (snapshot.state) {
      case 'paused':
        console.log('Upload is paused');
        break;
      case 'running':
        console.log('Upload is running');
        break;
    }
  }, 
  (error) => {
    
  }, 
  () => {
    
    getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
      console.log('File available at', downloadURL);
    });
  }
);
    }

    render(){
        return(
            <div>
                <div style={{"textAlign":"center", "margin":"10px"}}>
                    <div className="fileupload">
                       <label for="file-upload">
                          <img src={upload}  className='mainpage_uploadicon'/>
                        </label>   
                    <input id="file-upload"type="file"/>
                    </div>
                </div>
                {
                    this.state.postArray.map((item,index)=>(
                        <Post id={item.postId} userName={item.userName} postImage={item.postImageURL} likes={item.likes}/>   
                    ))
                }
                
            </div>
        );
        
    } 
}
        export default MainPage;
