import React, {Component} from 'react'
import "./StatusBar.css";
import statusimg from "../../images/images/pp1.png";
import {Avatar} from '@material-ui/core';
import uploadImg from "../../images/images/statusadd.png"
import img from "../../images/images/pp3.jpeg"

class StatusBar extends Component{
    constructor(props){
        super(props);
        this.state={
            statusList: []
            
        }
    }
    componentDidMount(){
        this.getData();
    }
    getData=()=>{
        let data=[
            {"username":"khush",
             "imageURL" :"../../images/images/Ninja.webp"},
            {"username":"Muskan",
             "imageURL" :"../../images/images/play.png"},
             {"username":"Raju",
             "imageURL" : "../../images/images/pp3.jpeg"},
             {"username":"khushi",
             "imageURL" :"../../images/images/pp4.png"},
             {"username":"Rohit",
             "imageURL" :"../../images/images/pp1.png"}       
        ]
        this.setState({statusList:data});

    }

    render(){
        return(
            <div >
                <div className="statusbar_container">
                    <img src={uploadImg} className="statusbar_upload" width="55px" height="55px"/>
                    {
                        this.state.statusList.map((item,index)=>(
                        <div className="status">
                            <Avatar className="statusbar_status" src={img}/>
                            <div className="statusbar_text">
                                 {item.username}
                            </div>
                        </div>

                        ))
                    }
                    
                    
                    
                    
                </div>
              
            </div>
        );
        
    } 
}
export default StatusBar;
